INSERT INTO questions (category, dimension, content, caption, score_weight, mapping_code, display_order) VALUES
('stage', 'D1', '내 아이디어를 좋아할 사람들이 전국에 몇 명인지 믿을 만한 통계로 계산해봤다.', '[시장 규모] 객관적 데이터 기반의 TAM/SAM/SOM 산출', 1.5, 'P', 1),
